import { openAlertMessage } from "./alertActions"
import  * as api   from "../../api"
export const friendActions = {
    SET_FRIENDS: "FRIENDS.SET_FRIENDS",
    SET_PENDING_FRIENDS_INVITATIONS: "FRIENDS.SET_PENDING_FRIENDS_INVITATIONS",
    SET_ONLINE_USERS: "FRIENDS.SET_ONLINE_USERS"
}



export const getActions = (dispatch) => {
    return {
        sendFriendInvitation: (data, closeDialogHandler) => 
        dispatch(sendFriendInvitation(data, closeDialogHandler))
    }
}

const sendFriendInvitation = (data, closeDialogHandler) => {

    return async (dispatch) => {
        const response = await api.sendFriendInvitation(data)
        console.log(response,"resssssssssssssss")
        if(response.error) {

            dispatch(openAlertMessage(response.exception?.response?.data));

        }else{
            dispatch(openAlertMessage(response?.data));
            closeDialogHandler()

        }
    }
}

export const setPendingFriendsInvitations = (setPendingFriendsInvitations) => {
    console.log(setPendingFriendsInvitations,"")
    return {
        type: friendActions.SET_PENDING_FRIENDS_INVITATIONS,
        setPendingFriendsInvitations,
    };
};
